INSERT INTO mos_content VALUES (21, 'Findlay: "You''ve done a grand job, Daddy"', '', 'Findlay is very appreciative of a job well done, things neat and tidy.  So, when he observed me running my hand to level the soil across a newly dug flowerbed (prepared for the sweet peas), he exclaimed: "Ooh, smoooooth - you''ve done a grand job, Daddy".  Not something Estelle or I say, so he''s picked it up somewhere else!', '', 1, 1, 0, 73, '2005-06-20 00:00:00', 62, '', '2005-07-21 12:03:12', 62, 0, '0000-00-00 00:00:00', '2005-07-21 00:00:00', '0000-00-00 00:00:00', '', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=', 2, 0, 1, '', '', 0, 78);
