INSERT INTO mos_content VALUES (30, 'Gospel Preaching Podcast', '', 'If you would like to receive Gospel Preachings to your MP3 player, follow these steps:\r\n\r\n<ol>\r\n<li>Download and install <a href="http://juicereceiver.sourceforge.net/index.php">Juice</a></li>\r\n<li>Add feed <a href="http://feeds.feedburner.com/gospelpreaching">http://feeds.feedburner.com/gospelpreaching</a></li>\r\n<li>If necessary, configure your music device to look in the Juice directory for new files and automatically add them to your player - I use <a href="http://dmzweb4.europe.creative.com/SRVS/CGI-BIN/WEBCGI.EXE/,/?St=212,E=0000000000104297056,K=3104,Sxi=0,Case=obj(11386),Kb=ww_english_add,VARSET=ws:http://uk.europe.creative.com/">Creative MediaPlayer</a></li>\r\n</ol>\r\n\r\nEnjoy!', '', 1, 1, 0, 69, '2006-02-13 09:50:04', 62, '', '2006-05-03 12:52:46', 62, 0, '0000-00-00 00:00:00', '2006-02-13 00:00:00', '0000-00-00 00:00:00', '', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=', 3, 0, 1, '', '', 0, 41);
