(274, 1, 1, 'HERR Jesu, komm!'),
(274, 1, 2, 'Nimm Deinen rechten Platz'),
(274, 1, 3, 'Als Mittelpunkt - des Menschen Sohn -'),
(274, 1, 4, 'Komm, richte auf den Herrschaftsthron!'),
(274, 1, 5, 'Herr Jesu, komm!'),
(274, 2, 1, 'Herr Jesu, komm!'),
(274, 2, 2, 'Der Mann der Schmerzen einst;'),
(274, 2, 3, 'Mann der Geduld, jetzt wartest Du,'),
(274, 2, 4, 'Du Mann der Freude immerzu:'),
(274, 2, 5, 'Herr Jesu, komm!'),
(274, 3, 1, 'Herr Jesu, komm!'),
(274, 3, 2, 'Es beug sich jedes Knie,'),
(274, 3, 3, 'Und jede Zunge Dich bekenn,'),
(274, 3, 4, 'Dich freudig Segensherrscher nenn!'),
(274, 3, 5, 'Herr Jesu, komm!'),
(274, 4, 1, 'Der Geist, die Braut'),
