(287, 1, 3, 'Kein andrer Name ist gegeben,'),
(287, 1, 4, 'Der Hilf und Heil uns also beut;'),
(287, 1, 5, 'In Ihm sich Menschens?hne r?hmen,'),
(287, 1, 6, 'Der f?r Verlorne kam zu s?hnen.'),
(287, 2, 1, 'Der S?nder h?rt den einen Namen'),
(287, 2, 2, 'Und wird von seiner Schuld befreit;'),
(287, 2, 3, 'Er klingt gar s?? in seinen Ohren,'),
(287, 2, 4, 'Wie Sieg und Leben allezeit!'),
(287, 2, 5, 'Sein Herz von Freude ?berquillet'),
(287, 2, 6, 'Und Lobgesang allein ihn stillet.'),
(287, 3, 1, 'O Jesu! Wer kann w?rdig preisen'),
(287, 3, 2, 'Und Deinen Namen voll erh?hn?'),
(287, 3, 3, 'Wir singen froh der Liebe Weisen,'),
(287, 3, 4, 'Von Deinem Tod und Auferstehn.'),
(287, 3, 5, 'Bald Erd und Himmel Dich besingen,'),
(287, 3, 6, 'Vereint erhabnes Lob Dir bringen!'),
(288, 1, 1, 'DU tr?stest unsre Herzen, Herr,'),
(288, 1, 2, 'Mit Himmelslicht von droben her;'),
(288, 1, 3, 'Es spricht der Geist Dein Wort mit Macht,'),
(288, 1, 4, 'Er, der bei uns weilt in der Nacht.'),
(288, 2, 1, 'Wir h?ren Deiner Stimme Laut,'),
(288, 2, 2, 'O Herr, der Geist macht sie uns traut,'),
(288, 2, 3, 'Hilft uns, Gedanken zu verstehn,'),
(288, 2, 4, 'Die von Dir unser Herz durchwehn.'),
(288, 3, 1, 'Herr, Deine Liebe, was Du bist,'),
(288, 3, 2, 'Der Deinen Schatz und Reichtum ist;'),
(288, 3, 3, 'Noch in der W?ste, schmecken wir'),
(288, 3, 4, 'Schon Gnad und Herrlichkeit von Dir.'),
(288, 4, 1, 'Dein Lebensreich an Kraft und Freud'),
(288, 4, 2, 'Weit mehr als Eskols Traube beut;'),
(288, 4, 3, 'Erfrischung reichlich zu uns flie?t,');
INSERT INTO `hymn_line_de` (`hymn_no`, `vers_no`, `line_no`, `line_text`) VALUES (288, 4, 4, 'Wo hier nur W?ste um uns ist.'),
(288, 5, 1, 'Bald ist des Geistes Dienst erf?llt,'),
(288, 5, 2, 'Den Er so gn?dig aufrechthielt;'),
(288, 5, 3, 'Dann reden wir von Angesicht,'),
(288, 5, 4, 'O heiiger Herr, mit Dir im Licht.'),
(289, 1, 1, 'LASS uns, Herr Jesu, Gottes heiiges Lamm,'),
(289, 1, 2, 'Des Lebensblut am Kreuze f?r uns rann,'),
(289, 1, 3, 'Die Tiefen Deiner Liebe mehr verstehn,'),
(289, 1, 4, 'Um so durch Leiden, ja den Tod zu gehn!'),
(289, 2, 1, 'Nimm Du das Herz und la? es f?r und f?r'),
(289, 2, 2, 'Verschlossen allem sein, Herr, au?er Dir,'),
(289, 2, 3, 'Und, Dir ergeben, dr?cke unserm Lauf'),
(289, 2, 4, 'F?r immer Deiner Liebe Siegel auf.'),
(289, 3, 1, 'Ja, wie gesegnet, die so allezeit'),
(289, 3, 2, 'Dir nah, gesch?tzt an Deiner trauten Seit,'),
(289, 3, 3, 'Das Leben, all die Kraft von Dir empfahn,'),
(289, 3, 4, 'Mit Dir, Herr, stehn und mit Dir gehn voran!'),
(289, 4, 1, 'Erweitre Du uns den beschr?nkten Sinn,'),
(289, 4, 2, 'La?, Herr, uns schaun Dein Werk von Anbeginn -'),
(289, 4, 3, 'L?s schwere Zungen Du, hier kundzutun'),
(289, 4, 4, 'All Deiner Liebe Herrlichkeit und Ruhm!'),
(290, 1, 1, 'VON Jesu will ich singen'),
(290, 1, 2, 'Die Botschaft wahr und treu,'),
(290, 1, 3, 'Die lieblich mir und kostbar,'),
(290, 1, 4, 'So alt und doch so neu.'),
(290, 1, 5, 'Er kam aus h?chsten H?hen,'),
(290, 1, 6, 'Aus lichter Herrlichkeit.'),
(290, 1, 7, 'Von Jesu - hehre Kunde -'),
(290, 1, 8, 'Er kam, dem Tod geweiht!'),
(290, 2, 1, 'Ja, da litt Er, mein Heiland,'),
(290, 2, 2, 'Ertrug den Tod f?r mich.'),
(290, 2, 3, 'Er hat das Werk vollendet,'),
(290, 2, 4, 'Befreit mich ewiglich.'),
(290, 2, 5, 'Er trug all meine S?nden,'),
(290, 2, 6, 'Den Zorn und das Gericht;'),
(290, 2, 7, 'Durch Jesu Tod in Schande'),
(290, 2, 8, 'Ist Satans Macht zunicht.'),
(290, 3, 1, 'Der Herr ist auferstanden,'),
(290, 3, 2, 'Vorbei sind Weh und Leid,'),
(290, 3, 3, 'Sein Sitz im h?chsten Himmel,'),
(290, 3, 4, 'Er lebt in Ewigkeit.'),
(290, 3, 5, 'Und in dem Heilgen Geiste'),
(290, 3, 6, 'Macht Er mich hier ganz reich,'),
(290, 3, 7, 'Den Segen zu ererben'),
(290, 3, 8, 'Und Gottes Lieb zugleich.'),
(290, 4, 1, 'O Teil, voll Gunst und Leben,'),
(290, 4, 2, 'Schon jetzt f?r Gott zu sein'),
(290, 4, 3, 'In Ihm, dem gro?en Heiland,'),
(290, 4, 4, 'Durch Dessen Blut ich rein!'),
(290, 4, 5, 'Und bald kommt Er dann f?r mich'),
(290, 4, 6, 'Und f?hrt mich zu Sich heim,'),
(290, 4, 7, 'Wo ich im Sang der Liebe'),
(290, 4, 8, 'Von Jesu stimme ein.'),
(291, 1, 1, 'SIEH, Er steigt aus Himmelsweiten,'),
(291, 1, 2, 'Der den Fluch f?r S?nder trug;'),
(291, 1, 3, 'Myriaden Ihn begleiten'),
(291, 1, 4, 'Im Triumph, o welch ein Zug!'),
(291, 1, 5, 'Halleluja!'),
(291, 1, 6, 'Jesus kommt und kommt in Macht!'),
(291, 2, 1, 'Seht den Heiland, den Ersehnten,'),
(291, 2, 2, 'Er erscheint im Feierkleid,'),
(291, 2, 3, 'Mit Ihm, die man einst verh?hnte,'),
(291, 2, 4, 'Teilend Seine Herrlichkeit.'),
(291, 2, 5, 'Halleluja!'),
(291, 2, 6, 'Gottes Sohn ist''s, der erscheint.'),
(291, 3, 1, 'Dann wird Israel Ihn sehen,'),
(291, 3, 2, 'Huldvoll und in Majest?t,'),
(291, 3, 3, 'Und alsbald ergreifet Wehen,'),
(291, 3, 4, 'Die Ihn einst verkannt, verschm?ht;'),
(291, 3, 5, 'Den Messias'),
(291, 3, 6, 'Werden sie verherrlicht schaun.'),
(291, 4, 1, 'Herr, die Braut ist mit dem Geiste'),
(291, 4, 2, 'In dem Ruf: Herr Jesu, komm!'),
(291, 4, 3, 'All die Herrlichkeit zu erben'),
(291, 4, 4, 'Und zu sitzen auf dem Thron.'),
(291, 4, 5, 'Alle Sch?pfung'),
(291, 4, 6, 'Seufzet, bis Du kommen wirst.'),
(291, 5, 1, 'Amen! Dich soll jeder preisen!'),
(291, 5, 2, 'Himmelsheere Ruhm Dir weihn!'),
(291, 5, 3, 'Macht und W?rde nun ergreife,'),
(291, 5, 4, 'Alle V?lker sind ja Dein!'),
(291, 5, 5, 'Komm, Herr Jesu!'),
(291, 5, 6, 'Halleluja! Komm, o Herr!'),
(292, 1, 1, 'O GOTT, an Majest?t erhaben,'),
(292, 1, 2, 'So gro? an Weisheit, Liebe, Macht,'),
(292, 1, 3, 'Der  Unverweslichkeit besitzet'),
(292, 1, 4, 'Und Licht zu Seiner Wohnstatt macht,'),
(292, 1, 5, 'Dir sei der Herzen Lob geweiht,'),
(292, 1, 6, 'Macht, Ehre Dir und Herrlichkeit!'),
(292, 2, 1, 'Im Lichtglanz Deines Wohlgefallens'),
(292, 2, 2, 'Die Segenswelt sich dehnet weit,'),
(292, 2, 3, 'Und wird auf ewig fortbestehen'),
(292, 2, 4, 'Zu Deiner Wonne, Deiner Freud;'),
(292, 2, 5, 'O hoher Sinn, der dies erdacht,'),
(292, 2, 6, 'O hehre Kraft, die es vollbracht!'),
(292, 3, 1, 'Gesegnet und erf?llt mit Staunen'),
(292, 3, 2, 'Stehn wir vor Dir mit Freudigkeit,'),
(292, 3, 3, 'Als S?hne Deine Ruhe teilen'),
(292, 3, 4, 'Zu Deiner Gnade Herrlichkeit;'),
(292, 3, 5, 'Dir, Gott, in allem alles Du,'),
(292, 3, 6, 'Kommt ewiglich Anbetung zu!'),
(293, 1, 1, 'DIE Gnade, die die Tiefen all des Wehs'),
(293, 1, 2, 'Von Golgatha erma? und f?r uns starb,'),
(293, 1, 3, 'Ber?hrt uns tief und das Verlangen weckt,'),
(293, 1, 4, 'Da? unser alles Du, der uns erwarb!'),
(293, 2, 1, 'Herr, Deine Herrlichkeit dort in der H?h,'),
(293, 2, 2, 'Wo Du erhaben in der Liebe Kreis,'),
(293, 2, 3, 'Spricht zu uns von der Sohnschaft Platz und N?h,'),
(293, 2, 4, 'Die uns der Vater gab zu Seinem Preis.'),
(293, 3, 1, 'All Deine Sch?nheit, Herr, Dein heiiger Wert'),
(293, 3, 2, 'Uns Erdenfreuden in den Schatten stellt;'),
(293, 3, 3, 'Das, was allein noch unser Herz begehrt,'),
(293, 3, 4, 'Bist Du, der Gegenstand in jener Welt.'),
(293, 4, 1, 'Die F?lle, Herr, an Licht und Liebe, die'),
(293, 4, 2, 'Kein Menschensinn beschreibt noch je ermi?t,'),
(293, 4, 3, 'In jener hehren Welt der Herrlichkeit,'),
(293, 4, 4, 'Die nahe r?ckt, bald voll entfaltet ist.'),
(293, 5, 1, 'Wenn alles ganz von Dir gesegnet ist,'),
(293, 5, 2, 'Ruht Gottes tiefe Liebe immerdar'),
(293, 5, 3, 'In dem, was Ihm von Dir, dem Sohne, spricht,'),
(293, 5, 4, 'Dann nimmt das Weltall Deine Gr??e wahr!'),
(294, 1, 1, 'HERR Jesu, Deine Liebe ohnegleichen'),
(294, 1, 2, 'In ihrer unerme?lich gro?en Kraft,'),
(294, 1, 3, 'Lie? diese heiige Stunde uns erreichen,'),
(294, 1, 4, 'Die unsern Herzen solche Freude macht.'),
(294, 2, 1, 'Von Deinem Liebesarm umfa?t, umgeben,'),
(294, 2, 2, 'Ist ihrem Haupt die Kirche zugetan;'),
(294, 2, 3, 'In ihrer Antwort ganz Dir hingegeben,'),
(294, 2, 4, 'Folgt sie Dir, wo, o Herr, Du f?hrest an.'),
(294, 3, 1, 'Du f?llest alles schon in dem Bereiche,'),
(294, 3, 2, 'Wo wir an Deiner Seite, Dir vertraut,'),
(294, 3, 3, 'Mit Dir f?r immer sind in Deiner N?he,'),
(294, 3, 4, 'Als Deine F?lle, Herr, Dein Leib, die Braut.'),
(294, 4, 1, 'Und jene St?tte droben in den H?hen'),
(294, 4, 2, 'Teilt die Versammlung ewiglich mit Dir!'),
(294, 4, 3, 'Ja, nach des Vaters Liebe ausersehen,'),
(294, 4, 4, 'Sind Seine Freud und Deine Wonne wir.'),
(295, 1, 1, 'DIR, gepriesner Geist, wir bringen'),
(295, 1, 2, 'Die Anbetung nun,'),
(295, 1, 3, 'Weisen dankbarer Verehrung,'),
(295, 1, 4, 'Dir sei Ruhm!'),
(295, 2, 1, 'Und in Deinem selgen Dienste'),
(295, 2, 2, 'Nun f?r Christi Braut,'),
(295, 2, 3, 'Wird durch Dich im reichsten Schmucke'),
(295, 2, 4, 'Sie geschaut.'),
(295, 3, 1, 'Seine Herrlichkeit erz?hlst Du,'),
(295, 3, 2, 'Kraft und Gnade gibst'),
(295, 3, 3, 'F?r die m?chtige Verwandlung,'),
(295, 3, 4, 'Die Du liebst.'),
(295, 4, 1, 'Dich, o Geist, wir nun anbeten,'),
(295, 4, 2, 'Wissen,  wer Du bist,'),
(295, 4, 3, 'Ehrfurchtsvoll aus unsern Herzen'),
(295, 4, 4, 'Lob Dir flie?t.'),
(296, 1, 1, 'LOBPREIS sei Dir, dem Sohne Gottes,'),
(296, 1, 2, 'Dem Heiligen, der droben wohnt;'),
(296, 1, 3, 'Vereint sind Liebe, Licht und Wahrheit'),
(296, 1, 4, 'In Dir, der hocherhaben thront,'),
(296, 1, 5, 'In dessen Antlitz in den H?hn'),
(296, 1, 6, 'Die Herrlichkeit wir strahlen sehn.'),
(296, 2, 1, 'O Liebe, die Dich, Herr, getrieben'),
(296, 2, 2, 'Ans Kreuz aus Gottheit-Herrlichkeit;'),
(296, 2, 3, 'Du kamst als Mensch, um zu besiegen'),
(296, 2, 4, 'Durch Deinen Tod den finstern Feind;'),
(296, 2, 5, 'Dein Sterben Satan niederzwang'),
(296, 2, 6, 'Und Dir auf ewig Ruhm errang!'),
(296, 3, 1, 'Wer mi?t an Deinem Kreuz die Tiefen,'),
(296, 3, 2, 'Die Liebe, die sich schlagen lie?,'),
(296, 3, 3, 'Als Gott Sein Schwert dort im Gerichte'),
(296, 3, 4, 'Auf Dein Haupt niederfahren lie?;'),
(296, 3, 5, 'In Finsternis warst Du allein,'),
(296, 3, 6, 'Die Wasser drangen auf Dich ein!'),
(296, 4, 1, 'Es ist vollbracht, das Werk vollendet,'),
(296, 4, 2, 'Der Vater, der verherrlicht ist,'),
(296, 4, 3, 'Hat aus dem Grabe Dich erwecket,'),
(296, 4, 4, 'Mit Herrlichkeit Dich froh begr??t'),
(296, 4, 5, 'Und Dich als Sieger hoch erh?ht'),
(296, 4, 6, 'Zur Rechten Seiner Majest?t.'),
(297, 1, 1, 'VATER, Sohn und Heilger Geist, Dir'),
(297, 1, 2, 'Wird in dieser Stund gebracht'),
(297, 1, 3, 'Preis, Anbetung, Reichtum, Ehre,'),
(297, 1, 4, 'Weisheit, Herrlichkeit und Macht.'),
(297, 2, 1, 'Du bist Gott durch alle Zeiten,'),
(297, 2, 2, 'Unnahbar im reinsten Licht,'),
(297, 2, 3, 'Unverweslich, doch in Jesu'),
(297, 2, 4, 'Strahlet uns  Dein Angesicht.'),
(297, 3, 1, 'S?hne, Fr?chte Deines Rates,'),
(297, 3, 2, 'Tadellos vor Dich gestellt,'),
(297, 3, 3, 'Preisen Deinen gro?en Namen,'),
(297, 3, 4, 'Da wir Christo zugesellt.'),
(297, 4, 1, 'Herrlichkeit in der Versammlung'),
(297, 4, 2, 'Sei in Christo Jesu Dir,'),
(297, 4, 3, 'Seiger Gott, Du Haupt von allem,'),
(297, 4, 4, 'Jetzt und bald auch f?r und f?r.'),
(298, 1, 1, 'HERR, anbetungsvoll verfolgen'),
(298, 1, 2, 'Deinen Pfad der Leiden wir -'),
(298, 1, 3, 'Jener letzten Stunden ?ngste'),
(298, 1, 4, 'Reden zu uns f?r und f?r.'),
(298, 2, 1, 'Tief war da, o Herr, Dein Kummer'),
(298, 2, 2, '- Unausdenkbar tief und schwer -,'),
(298, 2, 3, 'Welch ein Weh, welch heiige Leiden,'),
(298, 2, 4, 'Niemand schildert all ihr Heer!'),
(298, 3, 1, 'Niemand konnte mit Dir steigen'),
(298, 3, 2, 'In den abgrundtiefen Scho'),
(298, 3, 3, 'Deiner heiigen S?hnungsleiden -'),
(298, 3, 4, 'Dein, nur Dein war dieses Los!'),
(298, 4, 1, 'Du durchma?est die Entfernung,'),
(298, 4, 2, 'S?ndennacht und -fluch war Dein:'),
(298, 4, 3, 'In Verrat - von Gott verlassen -'),
(298, 4, 4, 'Strahlet Deiner Liebe Schein.'),
(299, 1, 1, 'O WAS wird''s sein, daheim bei Gott,'),
(299, 1, 2, 'Zu schauen Jesum voller Wonn,'),
(299, 1, 3, 'Der uns bekannt am Fremdlingsort'),
(299, 1, 4, 'Als Heiland und als Priester schon!'),
(299, 2, 1, 'Vor Christi Richterstuhl zu stehn,'),
(299, 2, 2, 'Wenn Er mit uns dann R?ckblick h?lt,'),
(299, 2, 3, 'Wenn wir in Seinem Lichte sehn,'),
(299, 2, 4, 'Was jede Stunde wiegt und z?hlt;'),
(299, 3, 1, 'Wo klar wir schaun wie nie zuvor'),
(299, 3, 2, 'Des Heiland-Gottes Gnadenmacht,'),
(299, 3, 3, 'Wie treu doch Seine Liebe war,'),
(299, 3, 4, 'Die uns geleitet durch die Nacht.'),
(299, 4, 1, 'Wenn Fremdlingszeit dahinten liegt,'),
(299, 4, 2, 'Sehn wir am himmlischen Gestad,'),
(299, 4, 3, 'Wie Liebe allem voll gen?gt,'),
(299, 4, 4, 'Im Morgenglanz von Christi Tag!'),
(300, 1, 1, 'O GOTTES Geist, Dir sei nun Preis geweiht,'),
(300, 1, 2, 'Denn Du bist Gott, der Heiige allezeit;'),
(300, 1, 3, 'Eins mit dem Vater und dem Sohn bist Du,'),
(300, 1, 4, 'Eins in dem Werk der Liebe immerzu.'),
(300, 2, 1, 'In tiefem Mitgef?hl kamst Du herab'),
(300, 2, 2, 'Und sahst, wie w?st und leer die Erde lag,'),
(300, 2, 3, 'Eh Gott den ausgedehnten Schauplatz schuf,'),
(300, 2, 4, 'Um Seinen Plan in Menschen kundzutun.'),
(300, 3, 1, 'Und als erf?llt die Zeit, der Sohn hier stand,'),
(300, 3, 2, 'Kamst Du, Ihn auszuzeichnen - unverwandt,'),
(300, 3, 3, 'Fortan Ihm zu Gebot als Seine Kraft,'),
(300, 3, 4, 'Auch noch, als Er Sich Selbst Gott dargebracht.'),
(300, 4, 1, 'Dem?tger noch - o welcher Liebe Bahn! -'),
(300, 4, 2, 'Bist Du, der vom erh?hten Christus kam'),
(300, 4, 3, 'Zu uns - was droben ist, erschlie?est Du'),
(300, 4, 4, 'Und f?hrst ein himmlisch Volk der Heimat zu.'),
(301, 1, 1, 'NICHTS, Herr, gleicht Deiner Herrlichkeit,'),
(301, 1, 2, 'Die unser Herz ber?hrt,'),
(301, 1, 3, 'Da? uns verlangt, Dir Selbst zu weihn'),
(301, 1, 4, 'Das Teil, das Dir geb?hrt.'),
(301, 2, 1, 'Viel Tausend, die geheiligt sind,'),
(301, 2, 2, 'Mit frohem Liebeslaut,'),
(301, 2, 3, 'Ganz eins, Dir ihre Lieder weihn -'),
(301, 2, 4, 'Die Antwort Deiner Braut.'),
(301, 3, 1, 'Lang harrtest Du der Freude Laut,'),
(301, 3, 2, 'Doch nun ist sie erf?llt;'),
(301, 3, 3, 'Verehrend gr??t Dich Deine Braut,'),
(301, 3, 4, 'Dein Lob allein sie stillt.'),
(301, 4, 1, 'Du, Herr, bist herrlich f?r und f?r,'),
(301, 4, 2, 'Die Braut auch lieblich ist,'),
(301, 4, 3, 'Genie?t der Liebe Ruh mit Dir,'),
(301, 4, 4, 'Wo Liebe  sie  umschlie?t.'),
(301, 5, 1, 'Wie wundersam ist Gottes Plan!'),
(301, 5, 2, 'Als Mensch hast Du die Braut,'),
(301, 5, 3, 'Die Dich befriedigt - eins mit Dir,'),
(301, 5, 4, 'Dir herrlich angetraut.'),
(302, 1, 1, 'O HERR, wir schaun in Herrlichkeit'),
(302, 1, 2, 'Dich nun im Glauben, beten an.'),
(302, 1, 3, 'Vor?ber zieht an unserm Geist,'),
(302, 1, 4, 'Was Du ertrugst am Kreuzesstamm.'),
(302, 2, 1, 'Unehre, Schm?hung h?ufte sich'),
(302, 2, 2, 'Auf Deine Stirn, dort dornumwebt;'),
(302, 2, 3, 'O welches Weh erfa?te Dich,'),
(302, 2, 4, 'Den ewge Ehre nun erhebt!'),
(302, 3, 1, 'Am Kreuz, des Mitgef?hls beraubt,'),
(302, 3, 2, 'Verlassen, einsam und allein,'),
(302, 3, 3, 'Doch nun, gekr?nt als Herr und Haupt,'),
(302, 3, 4, 'Die Himmel Lob und Preis Dir weihn.'),
(302, 4, 1, 'Verlie? Dich auch Dein Gott am Kreuz,'),
(302, 4, 2, 'Verbarg Sein Angesicht vor Dir,'),
(302, 4, 3, 'Schaun wir nun Seine Herrlichkeit'),
(302, 4, 4, 'In Deinem Antlitz f?r und f?r.'),
(302, 5, 1, 'Bewundernd schauen wir auf Dich,'),
(302, 5, 2, 'Gepriesner, teurer, heiiger Herr;'),
(302, 5, 3, 'M?g Erd und Himmel, eins im Lob,'),
(302, 5, 4, 'Dem Lamme bringen Ruhm und Ehr!'),
(303, 1, 1, 'WIE gro? ist unsre Schuld'),
(303, 1, 2, 'Bei Ihm, des Blut einst rann,'),
(303, 1, 3, 'Der uns gereinigt und gest?rkt,'),
(303, 1, 4, 'Um jetzt schon Gott zu nahn.'),
(303, 2, 1, 'Wir r?hmen, Heiland, Herr,'),
(303, 2, 2, 'Den Reichtum Deiner Gnad;'),
(303, 2, 3, 'Dein Gott ist auch der unsre nun,'),
(303, 2, 4, 'Dem wir anbetend nahn.'),
(303, 3, 1, 'Dein Vater unser ist,'),
(303, 3, 2, 'Wir beten Ihn nun an,'),
(303, 3, 3, 'Der uns den Ruf des Geistes gab'),
(303, 3, 4, 'Und uns zuvorerkannt.'),
(304, 1, 1, 'HERR, Deine Herrlichkeit zu sehn,'),
(304, 1, 2, 'Ist unsre tiefste Freude;'),
(304, 1, 3, 'Du stiegst vom Lichtglanz h?chster H?hn'),
(304, 1, 4, 'Herab zu uns als Zeuge'),
(304, 1, 5, 'Der Liebe hehr,'),
(304, 1, 6, 'Weit wie ein Meer,'),
(304, 1, 7, 'In der wir ewig leben'),
(304, 1, 8, 'Und Dich schon jetzt erheben.'),
(304, 2, 1, 'Gott gleich warst Du, Dein g?ttlich Sein'),
(304, 2, 2, 'Ist nimmer zu erkunden;'),
(304, 2, 3, 'Doch nahmst des Dieners Platz Du ein,'),
(304, 2, 4, 'Wardst hier als Mensch erfunden,'),
(304, 2, 5, 'Ja, als der Sohn,'),
(304, 2, 6, 'Zu Gottes Wonn;'),
(304, 2, 7, 'O Anblick sondergleichen,'),
(304, 2, 8, 'Zu sch?n, je zu verbleichen!'),
(304, 3, 1, 'Nach Golgatha gingst Du voran'),
(304, 3, 2, 'In Demut, der Geweihte,'),
(304, 3, 3, 'Des Vaters Willen Deine Bahn,'),
(304, 3, 4, 'Ihn kundzutun, Dir Freude;'),
(304, 3, 5, 'Das Kreuz ertrugst,'),
(304, 3, 6, 'Nach Leid nicht frugst,'),
(304, 3, 7, 'Da? Gottes Plan erf?llet'),
(304, 3, 8, 'Und Seine Lieb enth?llet.'),
(304, 4, 1, 'Herr, Deinen Namen mit Gesang'),
(304, 4, 2, 'Voll Freude wir erheben;'),
(304, 4, 3, 'Bald bricht der Tag der Herrschaft an,'),
(304, 4, 4, 'Wo Du regierst im Segen,'),
(304, 4, 5, 'Wenn weit und breit'),
(304, 4, 6, 'Dir Lob geweiht,'),
(304, 4, 7, 'Wenn Dir sich alle neigen,'),
(304, 4, 8, 'Und Ehrfurcht Dir erzeigen.'),
(305, 1, 1, 'O VATER, Deine Kinder z?hlen'),
(305, 1, 2, 'Auf Deine Hilf in dieser Stund,'),
(305, 1, 3, 'Auf Deinen Schutz traun unsre Seelen'),
(305, 1, 4, 'Und da? Dein Machtarm werde kund.'),
(305, 1, 5, 'O Gott, la? Segen uns empfahn,'),
(305, 1, 6, 'Da Deinem Gnadenthron wir nahn!'),
(305, 2, 1, 'Vor Dir wir alles niederlegen'),
(305, 2, 2, '- Vereint durch Gnade im Gebet -,'),
(305, 2, 3, 'Was kostbar Dir und angelegen,'),
(305, 2, 4, 'Nun unsre Herzen auch bewegt.'),
(305, 2, 5, 'O Vater, segne Du uns hier,'),
(305, 2, 6, 'Wir beugen uns mit Dank vor Dir.'),
(306, 1, 1, 'O HERR, wir sind versammelt,'),
(306, 1, 2, 'Die Schrift zu lesen hier,'),
(306, 1, 3, 'Du bist der Herr und Lehrer,'),
(306, 1, 4, 'Wir beugen uns vor Dir.'),
(306, 2, 1, 'Hilf uns nun, in dem Tempel'),
(306, 2, 2, 'Dich zu befragen, Herr!'),
(306, 2, 3, 'Die Wahrheit, die uns frei macht,'),
(306, 2, 4, 'Erschlie?e immer mehr!'),
(306, 3, 1, 'Durch Deinen Geist, Herr, lehre'),
(306, 3, 2, 'F?r heute uns Dein Wort;'),
(306, 3, 3, 'Mit Herz und Sinnen m?chten'),
(306, 3, 4, 'Gehorchen wir sofort.'),
(306, 4, 1, 'Gib durch Dein Wort uns Hilfe'),
(306, 4, 2, 'Und auch Ermutigung,'),
(306, 4, 3, 'Da? Gott verherrlicht werde'),
(306, 4, 4, 'Durch Dich in allem nun.'),
(307, 1, 1, 'GOTT wird auf hocherhabne Art'),
(307, 1, 2, 'Sein Wunderwerk vollziehn;'),
(307, 1, 3, 'Durch tiefe Wasser schreitet Er,'),
(307, 1, 4, 'F?hrt auf dem Sturm dahin.'),
(307, 2, 1, 'Nach unergr?ndlich tiefem Rat,'),
(307, 2, 2, 'Mit Weisheit, die nie irrt,'),
(307, 2, 3, 'Verfolgt Er Seinen Segensplan -'),
